interface UseSearchSubmissionReturn {
  submitSearch: (query: string) => Promise<void>;
  isSubmitting: boolean;
  error: Error | null;
}

export function useSearchSubmission(): UseSearchSubmissionReturn {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const { user } = useUsers();
  const router = useRouter();
  const jobStatus = useLeadJobStatus();

  const submitSearch = useCallback(async (query: string) => {
    setIsSubmitting(true);
    setError(null);
    jobStatus.reset();

    const timer = new LoadingTimer(1500);

    try {
      const exclusionResult = await checkExclusion(query);

      if (exclusionResult.isExcluded) {
        const jobId = await handleExcludedJob(exclusionResult, query, user);
        await timer.ensureMinimumDuration();
        router.push(`/results/${jobId}`);
        return;
      }

      const correlationId = await submitJobToBackend(query, user);
      await timer.ensureMinimumDuration();
      jobStatus.startPolling(correlationId);
      router.push(`/results/${correlationId}`);
    } catch (err) {
      await timer.ensureMinimumDuration();
      setError(err as Error);
    } finally {
      setIsSubmitting(false);
    }
  }, [user, router, jobStatus]);

  return { submitSearch, isSubmitting, error };
}

