const statusBadge = useMemo(() => {
  if (!job) return null;

  const status = job.overall_job_status || "unknown";
  const createdAt = job.created_at;

  // Check if job is stale
  const isJobStale = (): boolean => {
    const processingStates = [
      "pending_url_search",
      "url_worker_called",
      "scraper_worker_called",
      "queued",
      "in_progress",
    ];

    if (!processingStates.includes(status) || !createdAt) {
      return false;
    }

    const created = new Date(createdAt);
    const now = new Date();
    const ageInMinutes = (now.getTime() - created.getTime()) / (1000 * 60);
    return ageInMinutes > 10;
  };

  if (isJobStale()) {
    return <Badge variant="destructive">Timed Out</Badge>;
